import React, { useState, useEffect } from 'react';
import { Line, Pie, Bar } from 'react-chartjs-2';
import { CircularProgressbar } from 'react-circular-progressbar';
import { FiHome, FiBook, FiAward, FiCalendar, FiSettings, FiUsers, FiLogOut } from 'react-icons/fi';
import ChatButton from '../components/chat/Chatbutton';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('home');
  const [dashboardData, setDashboardData] = useState({
    profile: null,
    courses: [],
    achievements: [],
    events: [],
    leaderboard: []
  });

  const [mainContent, setMainContent] = useState(null);

  const fetchSectionData = async (section) => {
    try {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');
      
      const endpoints = {
        home: '/api/dashboard/overview',
        courses: '/api/dashboard/my-courses',
        achievements: '/api/dashboard/achievements',
        events: '/api/dashboard/events',
        community: '/api/dashboard/community',
        settings: '/api/dashboard/settings'
      };

      const response = await fetch(`${endpoints[section]}/${userId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setMainContent(data);
      }
    } catch (error) {
      console.error(`Error fetching ${section} data:`, error);
    }
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
    fetchSectionData(section);
  };

  const sidebarItems = [
    { id: 'home', label: 'Home', icon: <FiHome />, component: HomeContent },
    { id: 'courses', label: 'My Courses', icon: <FiBook />, component: CoursesContent },
    { id: 'achievements', label: 'Achievements', icon: <FiAward />, component: AchievementsContent },
    { id: 'events', label: 'Events', icon: <FiCalendar />, component: EventsContent },
    { id: 'community', label: 'Community', icon: <FiUsers />, component: CommunityContent },
    { id: 'settings', label: 'Settings', icon: <FiSettings />, component: SettingsContent }
  ];

  useEffect(() => {
    fetchSectionData(activeSection);
  }, []);

  const CurrentComponent = sidebarItems.find(item => item.id === activeSection)?.component || HomeContent;

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg">
        <div className="p-4">
          <div className="flex items-center space-x-3 mb-6">
            <img
              src={dashboardData.profile?.avatar_url || '/default-avatar.png'}
              alt="Profile"
              className="w-10 h-10 rounded-full"
            />
            <div>
              <h3 className="font-semibold">{dashboardData.profile?.name || 'Student Name'}</h3>
              <p className="text-sm text-gray-500">{dashboardData.profile?.level || 'Beginner'}</p>
            </div>
          </div>

          <nav className="space-y-1">
            {sidebarItems.map(item => (
              <button
                key={item.id}
                onClick={() => handleSectionChange(item.id)}
                className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                  activeSection === item.id
                    ? 'bg-blue-50 text-blue-600'
                    : 'text-gray-600 hover:bg-gray-50'
                }`}
              >
                {item.icon}
                <span>{item.label}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <header className="bg-white shadow-sm">
          <div className="max-w-7xl mx-auto px-4 py-4">
            <h1 className="text-2xl font-semibold text-gray-900">
              {sidebarItems.find(item => item.id === activeSection)?.label}
            </h1>
          </div>
        </header>

        <main className="max-w-7xl mx-auto px-4 py-6">
          <CurrentComponent data={mainContent} />
        </main>
      </div>
      <ChatButton />
    </div>
  );
};

// Content Components
const HomeContent = ({ data }) => {
  if (!data) return <div>Loading...</div>;
  
  return (
    <div className="space-y-8">
      {/* Stats Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl p-6 text-white">
          <h3 className="text-lg font-semibold mb-2">Learning Streak</h3>
          <div className="flex items-center justify-between">
            <span className="text-3xl font-bold">{data.streak} days</span>
            <div className="w-16 h-16">
              <CircularProgressbar
                value={data.streak}
                maxValue={30}
                styles={{
                  path: { stroke: '#fff' },
                  trail: { stroke: 'rgba(255,255,255,0.2)' },
                  text: { fill: '#fff', fontSize: '24px' }
                }}
              />
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl p-6 text-white">
          <h3 className="text-lg font-semibold mb-2">Course Progress</h3>
          <div className="flex items-center justify-between">
            <span className="text-3xl font-bold">{data.completedCourses}</span>
            <span className="text-lg">of {data.totalCourses} courses</span>
          </div>
          <div className="mt-4 w-full bg-white/20 rounded-full h-2">
            <div
              className="bg-white rounded-full h-2"
              style={{ width: `${(data.completedCourses / data.totalCourses) * 100}%` }}
            />
          </div>
        </div>

        <div className="bg-gradient-to-r from-emerald-500 to-emerald-600 rounded-xl p-6 text-white">
          <h3 className="text-lg font-semibold mb-2">Achievement Points</h3>
          <div className="flex items-center justify-between">
            <span className="text-3xl font-bold">{data.points}</span>
            <div className="text-4xl">🏆</div>
          </div>
          <p className="mt-2 text-sm">Level {data.level}</p>
        </div>
      </div>

      {/* Recent Activity Timeline */}
      <div className="bg-white rounded-xl shadow-sm p-6">
        <h3 className="text-lg font-semibold mb-4">Recent Activity</h3>
        <div className="space-y-4">
          {data.recentActivity?.map((activity, index) => (
            <div key={index} className="flex items-start space-x-4">
              <div className="flex-shrink-0 w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center">
                {activity.icon}
              </div>
              <div className="flex-1">
                <p className="font-medium">{activity.title}</p>
                <p className="text-sm text-gray-500">{activity.description}</p>
              </div>
              <span className="text-sm text-gray-400">{activity.time}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Recommended Courses */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-semibold mb-4">Recommended Courses</h3>
          <div className="space-y-4">
            {data.recommendedCourses?.map((course, index) => (
              <div key={index} className="flex items-center space-x-4 p-3 hover:bg-gray-50 rounded-lg transition-colors">
                <img src={course.thumbnail} alt={course.title} className="w-16 h-16 rounded-lg object-cover" />
                <div className="flex-1">
                  <h4 className="font-medium">{course.title}</h4>
                  <p className="text-sm text-gray-500">{course.duration} • {course.level}</p>
                </div>
                <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                  Start
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Learning Goals */}
        <div className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-semibold mb-4">Learning Goals</h3>
          <div className="space-y-4">
            {data.goals?.map((goal, index) => (
              <div key={index} className="flex items-center space-x-4">
                <input
                  type="checkbox"
                  checked={goal.completed}
                  className="w-4 h-4 text-blue-600 rounded"
                  readOnly
                />
                <div className="flex-1">
                  <p className="font-medium">{goal.title}</p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div
                      className="bg-blue-600 rounded-full h-2"
                      style={{ width: `${goal.progress}%` }}
                    />
                  </div>
                </div>
                <span className="text-sm text-gray-500">{goal.progress}%</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const CoursesContent = ({ data }) => {
  if (!data) return <div>Loading...</div>;
  return (
    <div className="grid gap-6">
      {data.courses?.map(course => (
        <div key={course.id} className="bg-white rounded-xl shadow-sm p-6">
          <h3 className="text-lg font-semibold">{course.name}</h3>
          <div className="mt-4">
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-blue-600 rounded-full h-2"
                style={{ width: `${course.progress}%` }}
              />
            </div>
            <p className="mt-2 text-sm text-gray-600">
              Progress: {course.progress}%
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};


const AchievementsContent = ({ data }) => {
  if (!data) return <div>Loading...</div>;
  return (
    <div className="grid grid-cols-3 gap-6">
      {data.achievements?.map(achievement => (
        <div key={achievement.id} className="bg-white rounded-xl shadow-sm p-6">
          <div className="flex items-center justify-center mb-4">
            <div className="w-16 h-16 rounded-full bg-yellow-100 flex items-center justify-center">
              {achievement.icon}
            </div>
          </div>
          <h3 className="text-lg font-semibold text-center">{achievement.name}</h3>
          <p className="text-sm text-gray-600 text-center mt-2">{achievement.description}</p>
        </div>
      ))}
    </div>
  );
};

const EventsContent = ({ data }) => {
  if (!data) return <div>Loading...</div>;
  return (
    <div className="grid gap-6">
      {data.events?.map(event => (
        <div key={event.id} className="bg-white rounded-xl shadow-sm p-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-semibold">{event.title}</h3>
            <span className="text-blue-600">{event.date}</span>
          </div>
          <p className="text-gray-600">{event.description}</p>
          <button className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg">
            Join Event
          </button>
        </div>
      ))}
    </div>
  );
};

const CommunityContent = ({ data }) => {
  if (!data) return <div>Loading...</div>;
  return (
    <div className="grid gap-6">
      {data.members?.map(member => (
        <div key={member.id} className="bg-white rounded-xl shadow-sm p-6 flex items-center">
          <img src={member.avatar} alt={member.name} className="w-12 h-12 rounded-full mr-4" />
          <div>
            <h3 className="font-semibold">{member.name}</h3>
            <p className="text-sm text-gray-600">{member.level}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

const SettingsContent = ({ data }) => {
  if (!data) return <div>Loading...</div>;
  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <h3 className="text-lg font-semibold mb-6">Account Settings</h3>
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">Email</label>
          <input
            type="email"
            defaultValue={data.email}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Notifications</label>
          <div className="mt-2">
            <label className="inline-flex items-center">
              <input type="checkbox" defaultChecked={data.notifications} />
              <span className="ml-2">Enable email notifications</span>
            </label>
          </div>
        </div>
      </div>
    </div>
  );
};


export default Dashboard;
